SignalType check_waveform_by_harmonic(double* mag, int base_freq_khz, int harmonic_order) {
    int base_index = (int)((float)base_freq_khz * 1000.0f * ADC_LEN / Fs + 0.5f);
    int harmonic_index = base_index * harmonic_order;
    float base_power = (float)mag[base_index - 1] + mag[base_index] + mag[base_index + 1];
    float harmonic_power = (float)mag[harmonic_index - 1] + mag[harmonic_index] + mag[harmonic_index + 1];
    if (harmonic_power == 0) { // 避免除以零
        return SINE;
    }
    if (harmonic_order == 3) {
        if (base_power > (float)(SINE_THRESHOLD_3RD_HARMONIC * harmonic_power)) {
            return SINE;
        }
    } else if (harmonic_order == 5) {
        if (base_power > (float)(SINE_THRESHOLD_5TH_HARMONIC * harmonic_power)) {
            return SINE;
        }
    }  
    return TRIANGLE;
}



//存频率、波形
void fft_cal_2types(void) {
    int i;
    double sum = 0;
    float average;
    for (i = 0; i < ADC_LEN; i++) {
	    indata[2 * i] = gADC_Buffer[i] *3.0/65535;
        sum += indata[2 * i];
    }
    average = sum / ADC_LEN;
    for (i = 0; i < ADC_LEN; i++) {
        indata[2 * i] = indata[2 * i]  - average; 
        indata[2 * i + 1] = 0;                    
    }
    arm_cfft_f32(&arm_cfft_sR_f32_len1024, indata, 0, 1);
    for (i = 0; i < ADC_LEN / 2; i++) {
        // 使用float防止32位系统中 uint16_t * uint16_t 的乘法溢出
        float real_part = indata[2 * i];
        float imag_part = indata[2 * i + 1];
        mag[i] = (float)(real_part * real_part + imag_part * imag_part);
    }

    int freq_khz_1 = gfrq_indices[0] * 5 + 10;
    int freq_khz_2 = gfrq_indices[1] * 5 + 10;
    Wave[0].Freq = freq_khz_1*1000;
    Wave[1].Freq = freq_khz_2*1000;

    gwave_type[0] = check_waveform_by_harmonic(mag, freq_khz_1, 3);
    gwave_type[1] = check_waveform_by_harmonic(mag, freq_khz_2, 3);
    if (freq_khz_1 * 3 == freq_khz_2) {
        gwave_type[0] = check_waveform_by_harmonic(mag, freq_khz_1, 5); 
    } else if (freq_khz_2 * 3 == freq_khz_1) {
        gwave_type[1] = check_waveform_by_harmonic(mag, freq_khz_2, 5);
    }
    Wave[0].Type = gwave_type[0];
    Wave[1].Type = gwave_type[1];

    Prepare_DAC_Buffer();

}



//正交解调？
double modulus(double I, double Q){
    double temp_double;
    temp_double = sqrt(I*I + Q*Q);
    return temp_double;
}


double corr1000_200(uint16_t* data, uint16_t* mask) {
	double sum=0;
    double res = 0;
    uint16_t i;
    for(i = 0; i < 1000 ; i++)
        sum = sum + data[i];
    sum = sum / 1000.0; 

    for(i = 0; i < 1000 ; i++)
        res = res + (data[i] - sum) * (mask[i%200] - 2048);
    return res;
}


//计算频率索引
void cal_2frqs(void) 
{
    double corr_sin_double;
    double corr_cos_double;
    unsigned char i, temp_char;
    for(i = 0; i < 19; i++)
    {
        corr_sin_double = corr1000_200(gADC_Buffer, DAC_SIN + 200*i);
        corr_cos_double = corr1000_200(gADC_Buffer, DAC_COS + 200*i);
        mag[i] = modulus(corr_sin_double, corr_cos_double);
    }
    unsigned char idx_max1 = 0;      // 最大幅值的索引
    unsigned char idx_max2 = 1;      // 次大幅值的索引

    if (mag[idx_max1] < mag[idx_max2]) {
        temp_char = idx_max1;
        idx_max1 = idx_max2;
        idx_max2 = temp_char;
    }
    for (i = 2; i < 19; i++) {
        if (mag[i] > mag[idx_max1]) {
            idx_max2 = idx_max1; // 原最大值成为次大值
            idx_max1 = i;
        } else if (mag[i] > mag[idx_max2]) {
            idx_max2 = i;
        }
    }
    if (idx_max1 > idx_max2) {
        gfrq_indices[0] = idx_max2;
        gfrq_indices[1] = idx_max1;
    } else {
        gfrq_indices[0] = idx_max1;
        gfrq_indices[1] = idx_max2;
    }
}



//这里要修改出三角波
void Prepare_DAC_Buffer(void) {
    uint16_t* src1 = DAC_SIN + 200 * gfrq_indices[0];
    uint16_t* src2 = DAC_SIN + 200 * gfrq_indices[1];
    for (int i = 0; i < WAVE_POINT; i++) {
        DAC_Buffer1[i] = src1[i % 200];
        DAC_Buffer2[i] = src2[i % 200];
    }
}



int get_start_index(double cos_comp,double sin_comp, float freq_hz) {
    double phase_angle = atan2(cos_comp,sin_comp);
    double pos_angle=phase_angle; 

    int wavetable_len = (int)(1000000.0f / freq_hz);
    float offset = pos_angle / (2 * PI) * wavetable_len;

    int idx = ((int)roundf(offset)) % wavetable_len;
    if (idx < 0) idx += wavetable_len;

    return idx;
}


