// 函数名称:void Write_8bit(u8 dat)
// 函数功能:AD9910串行口写入数据
// 入口参数:dat      8位数据
// 出口参数:无
//---------------------------------------------------------------------------------------------------------------------------------
void Write_8bit(u8 dat)
{
	u8 i, com;
	com = 0x80;
	AD9910_SCK_Clr;
	for (i = 0; i < 8; i++)
	{
		if ((dat & com) == 0)
			AD9910_SDI_Clr;
		else
			AD9910_SDI_Set;
		AD9910_SCK_Set;
		com = com >> 1;
		AD9910_SCK_Clr;
	}
}

//---------------------------------------------------------------------------------------------------------------------------------
// 函数名称:void Write_32bit(u32 dat)
// 函数功能:AD9910串行口写入数据
// 入口参数:dat      32位数据
// 出口参数:无
//---------------------------------------------------------------------------------------------------------------------------------
void Write_32bit(u32 dat)
{
	u8 i;
	u32 com;
	com = 0x80000000;
	AD9910_SCK_Clr;

	for (i = 0; i < 32; i++)
	{
		if ((dat & com) == 0)
			AD9910_SDI_Clr;
		else
			AD9910_SDI_Set;

		AD9910_SCK_Set;
		com = com >> 1;
		AD9910_SCK_Clr;
	}
}

